mapper.select:
select core.event.id,core.event.json
FROM core.event , core.event_metadata where core.event.id=core.event_metadata.event_id
and server_version::BIGINT >= #{serverVersion,jdbcType=BIGINT}  and
base_entity_id=any(
select base_entity_id from core.client_metadata
where


	address3=any(array

		#{listItem}

	)

)
order by core.event_metadata.server_version asc
LIMIT #{limit}

select

from core.event_metadata em
join core.event e on em.event_id = e.id
where ( em.openmrs_uuid is null or em.openmrs_uuid ='' )
	and em.event_type = #{eventType,jdbcType=VARCHAR}
	and em.server_version between #{from,jdbcType=BIGINT} and #{to,jdbcType=BIGINT} 
	and em.date_deleted is null
LIMIT #{limit}

select

	distinct


from core.event_metadata em
inner join core.event e on em.event_id = e.id




	order by em.${example.orderByClause}

LIMIT #{limit} OFFSET #{offset}
